mapper.select:
SELECT concat(p.brand, ' ', p.name) name, ssr.out_qty # Id 가 null 일 경우 전체 가게의 베스트 상품을 조회
FROM user_store us JOIN store_stock_report ssr ON us.id = ssr.user_store_id JOIN product p ON ssr.product_id = p.id WHERE us.id = #{Id}
FROM store_stock_report ssr JOIN product p ON ssr.product_id = p.id
ORDER BY ssr.out_qty DESC LIMIT 5;
